Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_TOPRIGHT", 0, 0);
if (self.objectType == "item") then
	GameTooltip:SetQuestItem(self.type, self:GetID());
	GameTooltip_ShowCompareItem(GameTooltip);
elseif (self.objectType == "currency") then
	GameTooltip:SetQuestCurrency(self.type, self:GetID());
end
CursorUpdate(self);

Scripts.OnClick:
if ( IsModifiedClick() and self.objectType == "item") then
	HandleModifiedItemClick(GetQuestItemLink(self.type, self:GetID()));
else
	if ( self.container and self.container.chooseItems and self.type == "choice" ) then
		self.container.itemChoice = self:GetID();
		self.highlight:SetPoint('TOPLEFT', self, 0, 0);
		self.highlight:Show();
		self.highlight.InAnim:Stop();
		self.highlight.InAnim:Play();
		self.InAnim:Stop();
		self.InAnim:Play();
	end
end

Scripts.OnDoubleClick:
if ( not IsModifiedClick() and self.container and self.container.chooseItems and self.type == "choice" ) then
	GetQuestReward(self:GetID());
end

Scripts.OnClick:
if ( IsModifiedClick("CHATLINK") and self.spellID) then
	local spellLink = GetSpellLink(self.spellID);
	ChatEdit_InsertLink(spellLink);
end

Scripts.OnLoad:
-- add tables for column frames, used when drawing qItems as tooltips
self.Choices.Columns = {};
self.Extras.Columns = {};
self.Active = {};

self.parent = self:GetParent();
self.ignoreRegions = true;
self:EnableMouse(true);

-- set parent/strata on load; main frame keeps table key, strata correctly draws over everything else.
self:SetParent(UIParent);
self:SetFrameStrata('FULLSCREEN_DIALOG');

self.Items = {};
self:SetScale(1.1);

local r, g, b = GetClassColor(select(2, UnitClass("player")));
self.Background:SetColorTexture(1, 1, 1);
self.Background:SetGradientAlpha("VERTICAL", 0, 0, 0, 0.75, r / 5, g / 5, b / 5, 0.75);

self.tooltipFramePool = CreateFramePool("GameTooltip", self, "ImmersionItemTooltipTemplate", function(self, obj) obj:Hide(); end);
self.tooltipFramePool.creationFunc = function(framePool)
	local index = #framePool.inactiveObjects + framePool.numActiveObjects + 1
	return CreateFrame(framePool.frameType, "Immersion"..framePool.frameType..index, framePool.parent, framePool.frameTemplate);
end;

Scripts.OnEnter:
if (self.tooltip) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip, HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
end

Scripts.OnLoad:
self.buttonTemplate = "ImmersionItemButtonTemplate, ImmersionRewardItemCodeTemplate";
self.spellRewardPool = CreateFramePool("BUTTON", self, "ImmersionQuestSpellTemplate, ImmersionRewardSpellCodeTemplate");
self.followerRewardPool = CreateFramePool("BUTTON", self, "LargeQuestInfoRewardFollowerTemplate");
self.spellHeaderPool = CreateFontStringPool(self, "BACKGROUND", 0, "QuestInfoSpellHeaderTemplate");

ImmersionQuestInfoItem1.container = self:GetParent():GetParent()
ImmersionQuestInfoItem1.highlight = self.ItemHighlight

Scripts.OnShow:
self:SetAlpha(1);
if (not self.Text:IsSequence()) then
	self:Hide();
end

Scripts.OnUpdate:
self.throttle = self.throttle + elapsed;
if self.throttle > 0.015 then
	local value = self.Text:GetCurrentProgress();
	local modifiedValue = value or 1;
	self:SetValue(modifiedValue);
	if self.Text:GetNumRemaining() > 1 then
		self:SetAlpha(1);
	else 
		self:SetAlpha(1 - (modifiedValue)^8);
	end
	if not value then
		self:Hide();
	end
	self.throttle = 0;
end

Scripts.OnLoad:
self.throttle = 0;
self.Text = self:GetParent().TextFrame.Text;
self:SetStatusBarTexture(1, 1, 1, "BORDER", -1);
self:GetStatusBarTexture():SetGradient("VERTICAL", 8/255, 93/255, 72/255, 11/255, 136/255, 105/255);
self:SetStatusBarColor(1, 0.8, 0);
self:SetMinMaxValues(0, 1);
self:SetFrameLevel(self:GetFrameLevel() - 1)

Scripts.OnShow:
local id, rep, maxRep, name, text, texture, reaction, threshold, nextThreshold = ImmersionAPI:GetFriendshipReputation();
if ( id and id > 0 ) then
	-- if max rank, make it look like a full bar
	if ( not nextThreshold ) then
		threshold, nextThreshold, rep = 0, 1, 1;
	end
	if ( texture ) then
		self.icon:SetTexture(texture);
	else
		self.icon:SetTexture("Interface\\Common\\friendship-heart");
	end
	self:SetMinMaxValues(threshold, nextThreshold);
	self:SetValue(rep);
	self:Show();
else
	self:Hide();
end
